import React, {useRef, useState, useEffect} from 'react';
import { useHistory } from 'react-router-dom';
import { ChatEngine } from 'react-chat-engine';
import {auth} from '../firebase';
import { useAuth } from '../contexts/AuthContext';
import axios from 'axios';
import { setLogLevel } from 'firebase';

const Chats=() =>{

    const history = useHistory();
    const {user} =useAuth();
    const [loading, setLoading] =useState(true);

    const handleLogout = async () => {
        await auth.signOut();
        history.push('/');  //renavigating to login form after logout
    }

    const getFile = async (url) =>{
        const response =await fetch(url);
        const data = await response.blob();
        return new File([data],"userPhoto.jpg",{type:'image/jpeg'})
    }
    useEffect(() =>{
        if(!user){
            history.push('/');
            return ;
        }
        axios.get('http://api.chatengine.io/users/me',{
            headers:{
                "project-id":"50364033-27a6-41ad-9d13-fa712fcca418",
                "user-name":user.email,
                "user-secret":user.uid,
            }
        })
        .then(()=>{
            setLoading(false);
        })
        .catch(()=>{
            let formdata= new FormData();
            formdata.append('email',user.email);
            formdata.append('username',user.displayName);
            formdata.append('secret',user.uid);

            getFile(user.photoURL)
                .then((avatar)=>{
                    formdata.append('avatar',avatar,avatar.name)
                    axios.post('http://api.chatengine.io/users/',
                       formdata, 
                       {
                           headers:{"private-key":"3e51ae9b-f211-4499-873e-37841aef1119"}
                       }
                    )
                    .then(()=>setLoading(false))
                    .catch((error)=>console.log(error))
                })
        })
    },[user, history])

    if(!user || loading ) return 'Loading...';
    return(
        <div className="chats-page">
            <div className="nav-bar">
                <div className="logo-tab">
                    Microsoft Teams
                </div>
                <div onClick={handleLogout} className="logout-tab">
                    Logout
                </div>
            </div>
            
            <ChatEngine
                height="calc(100vh-66px)"
                projectID="50364033-27a6-41ad-9d13-fa712fcca418"
                userName={user.email}
                userSecret={user.uid}
            />
        </div>

    );
}

export default Chats;
